select max(distinct(ac_x)) into xmax from area_coordenadas where ubi_id = arg;
select min(distinct(ac_x)) into xmin from area_coordenadas where ubi_id = arg;
select max(distinct(ac_y)) into ymax from area_coordenadas where ubi_id = arg;
select min(distinct(ac_y)) into ymin from area_coordenadas where ubi_id = arg;
                IF xmax >= x then
     SET B1 = TRUE;
     END IF;
                IF  xmin <= x then
     SET B2 = TRUE;
     END IF;
     IF ymax >= y then
